{"filePath": "c:\Microsoft\agent-framework\dotnet\samples\GettingStarted\FoundryAgents\FoundryAgents_Step15_ComputerUse\ProgramEnhanced.cs", "content": "// Copyright (c) Microsoft. All rights reserved.

// Enhanced Computer Use Demo - Form Filling Task
// This shows a more realistic use case: AI agent filling out a web form autonomously

using Azure.AI.Projects;
using Azure.AI.Projects.OpenAI;
using Azure.Identity;
using Microsoft.Agents.AI;
using Microsoft.Extensions.AI;
using OpenAI.Responses;

namespace Demo.ComputerUse.Enhanced;

internal sealed class ProgramEnhanced
{
    private static async Task Main(string[] args)
    {
        string endpoint = Environment.GetEnvironmentVariable("AZURE_FOUNDRY_PROJECT_ENDPOINT") ?? throw new InvalidOperationException("AZURE_FOUNDRY_PROJECT_ENDPOINT is not set.");
        const string DeploymentName = "computer-use-preview";

        AIProjectClient aiProjectClient = new(new Uri(endpoint), new AzureCliCredential());

        const string AgentInstructions = @"
            You are an intelligent form-filling assistant. 

            Your task is to:
            1. Look at the form on the screen
            2. Fill in all required fields with the information provided
            3. Validate that all fields are completed correctly
            4. Submit the form

            Be methodical and explain what you're doing at each step.
            After each action, describe what you see on the screen.
        ";

        Console.WriteLine("=== Enhanced Computer Use Demo: Autonomous Form Filling ===\n");
        Console.WriteLine("This demo shows an AI agent that can:");
        Console.WriteLine("  â€¢ Understand complex web forms visually");
        Console.WriteLine("  â€¢ Make decisions about which fields to fill");
        Console.WriteLine("  â€¢ Navigate multi-step processes autonomously");
        Console.WriteLine("  â€¢ Validate its own work\n");

        AIAgent agent = await aiProjectClient.CreateAIAgentAsync(
            name: "FormFillingAgent",
            model: DeploymentName,
            instructions: AgentInstructions,
            description: "AI agent that can autonomously fill out web forms by viewing screenshots.",
            tools: [
                ResponseTool.CreateComputerTool(ComputerToolEnvironment.Browser, 1280, 720).AsAITool(),
            ]);

        try
        {
            await RunFormFillingTaskAsync(agent);
        }
        finally
        {
            await aiProjectClient.Agents.DeleteAgentAsync(agent.Name);
        }
    }

    private static async Task RunFormFillingTaskAsync(AIAgent agent)
    {
        // In a real scenario, these would be actual screenshots
        // For this demo, we'll simulate with descriptions
        Dictionary<string, byte[]> screenshots = LoadFormScreenshots();

        ChatOptions chatOptions = new();
        ResponseCreationOptions responseCreationOptions = new()
        {
            TruncationMode = ResponseTruncationMode.Auto
        };
        chatOptions.RawRepresentationFactory = (_) => responseCreationOptions;
        ChatClientAgentRunOptions runOptions = new(chatOptions)
        {
            AllowBackgroundResponses = true,
        };

        AgentThread thread = agent.GetNewThread();

        // Define the task with user data to fill
        string taskDescription = @"
Please fill out this customer registration form with the following information:

Name: John Smith
Email: john.smith@example.com
Phone: (555) 123-4567
Company: Acme Corporation
Country: United States
Subscribe to newsletter: Yes

After filling all fields, click the Submit button.
";

        ChatMessage message = new(ChatRole.User, [
            new TextContent(taskDescription),
            new DataContent(new BinaryData(screenshots["empty_form"]), "image/png")
        ]);

        Console.WriteLine("ðŸ“‹ Task: Fill out customer registration form");
        Console.WriteLine("ðŸ“¸ Showing agent the initial screenshot...\n");

        AgentRunResponse runResponse = await agent.RunAsync(message, thread: thread, options: runOptions);

        // Track what the agent is doing
        int iteration = 0;
        const int MaxIterations = 15;
        FormState currentState = FormState.EmptyForm;
        string initialCallId = string.Empty;
        List<string> completedFields = new();

        while (true)
        {
            // Poll for completion
            while (runResponse.ContinuationToken is { } token)
            {
                await Task.Delay(TimeSpan.FromSeconds(2));
                runOptions.ContinuationToken = token;
                runResponse = await agent.RunAsync(thread, runOptions);
            }

            if (iteration >= MaxIterations)
            {
                Console.WriteLine($"\nâš ï¸  Reached maximum iterations ({MaxIterations}). Stopping.");
                break;
            }

            iteration++;
            Console.WriteLine($"\n{'=',50}");
            Console.WriteLine($"ITERATION {iteration}");
            Console.WriteLine($"{'=',50}");

            // Get any text responses from the agent
            var textContents = runResponse.Messages
                .SelectMany(m => m.Contents)
                .Where(c => c is TextContent)
                .Cast<TextContent>();

            foreach (var textContent in textContents)
            {
                if (!string.IsNullOrWhiteSpace(textContent.Text))
                {
                    Console.WriteLine($"\nðŸ’­ Agent's thoughts: {textContent.Text}");
                }
            }

            // Check for computer actions
            IEnumerable<ComputerCallResponseItem> computerCalls = runResponse.Messages
                .SelectMany(x => x.Contents)
                .Where(c => c.RawRepresentation is ComputerCallResponseItem and not null)
                .Select(c => (ComputerCallResponseItem)c.RawRepresentation!);

            ComputerCallResponseItem? firstCall = computerCalls.FirstOrDefault();
            if (firstCall is null)
            {
                Console.WriteLine("\nâœ… Agent finished the task!");
                Console.WriteLine($"\nðŸ“Š Summary:");
                Console.WriteLine($"   â€¢ Total iterations: {iteration}");
                Console.WriteLine($"   â€¢ Fields completed: {completedFields.Count}");
                if (completedFields.Any())
                {
                    Console.WriteLine($"   â€¢ Completed fields: {string.Join(", ", completedFields)}");
                }
                break;
            }

            // Process the action
            ComputerCallAction action = firstCall.Action;
            string currentCallId = firstCall.CallId;

            if (string.IsNullOrEmpty(initialCallId))
            {
                initialCallId = currentCallId;
            }

            Console.WriteLine($"\nðŸŽ¬ Action: {action.Kind}");

            // Show detailed action info
            string fieldName = "";
            if (action.TypeText is not null)
            {
                Console.WriteLine($"   ðŸ“ Typing: '{action.TypeText}'");
                fieldName = DetectFieldFromText(action.TypeText);
                if (!string.IsNullOrEmpty(fieldName) && !completedFields.Contains(fieldName))
                {
                    completedFields.Add(fieldName);
                }
            }
            if (action.KeyPressKeyCodes is not null)
            {
                Console.WriteLine($"   âŒ¨ï¸  Keys: {string.Join(", ", action.KeyPressKeyCodes)}");
            }

            // Simulate the action and get next screenshot
            var result = SimulateFormAction(action, currentState, screenshots, completedFields);
            currentState = result.State;

            Console.WriteLine($"   ðŸ“¸ Current state: {currentState}");
            Console.WriteLine($"   ðŸ“ˆ Progress: {completedFields.Count}/6 fields completed");

            // Send back the result
            AIContent content = new()
            {
                RawRepresentation = new ComputerCallOutputResponseItem(
                    initialCallId,
                    output: ComputerCallOutput.CreateScreenshotOutput(new BinaryData(result.Screenshot), "image/png"))
            };

            message = new(ChatRole.User, [content]);
            runResponse = await agent.RunAsync(message, thread: thread, options: runOptions);
        }
    }

    private static string DetectFieldFromText(string text)
    {
        if (text.Contains("John") || text.Contains("Smith")) return "Name";
        if (text.Contains("@")) return "Email";
        if (text.Contains("555") || text.Contains("123")) return "Phone";
        if (text.Contains("Acme")) return "Company";
        if (text.Contains("United States")) return "Country";
        return "";
    }

    private static (FormState State, byte[] Screenshot) SimulateFormAction(
        ComputerCallAction action,
        FormState currentState,
        Dictionary<string, byte[]> screenshots,
        List<string> completedFields)
    {
        string actionType = action.Kind.ToString().ToLower();

        // Determine next state based on action
        if (actionType.Contains("click"))
        {
            if (completedFields.Count >= 6)
            {
                return (FormState.Submitted, screenshots["success"]);
            }
            // Clicked a field to focus it
            return (FormState.FillingFields, screenshots["form_in_progress"]);
        }

        if (actionType.Contains("type") && action.TypeText is not null)
        {
            // Typing into a field
            return (FormState.FillingFields, screenshots["form_in_progress"]);
        }

        if (actionType.Contains("key"))
        {
            // Tab to next field or Enter to submit
            if (completedFields.Count >= 6)
            {
                return (FormState.Submitted, screenshots["success"]);
            }
            return (FormState.FillingFields, screenshots["form_in_progress"]);
        }

        return (currentState, screenshots["empty_form"]);
    }

    private static Dictionary<string, byte[]> LoadFormScreenshots()
    {
        // In a real implementation, these would be actual PNG files
        // For this demo, we'll create placeholder data
        var screenshots = new Dictionary<string, byte[]>
        {
            ["empty_form"] = CreatePlaceholderImage("Empty Registration Form"),
            ["form_in_progress"] = CreatePlaceholderImage("Form Being Filled"),
            ["success"] = CreatePlaceholderImage("Success - Form Submitted!")
        };
        return screenshots;
    }

    private static byte[] CreatePlaceholderImage(string description)
    {
        // Return minimal PNG data - in real scenario, load actual screenshots
        return new byte[] { 0x89, 0x50, 0x4E, 0x47 }; // PNG header
    }
}

internal enum FormState
{
    EmptyForm,
    FillingFields,
    Submitted
}
"}

