using Microsoft.Extensions.AI;
using Microsoft.Playwright;
using OpenAI.Responses;

namespace Demo.ComputerUse.Real;

internal sealed class ProgramReal
{
    private static async Task Main(string[] args)
    {
        string endpoint = Environment.GetEnvironmentVariable("AZURE_FOUNDRY_PROJECT_ENDPOINT") ?? throw new InvalidOperationException("AZURE_FOUNDRY_PROJECT_ENDPOINT is not set.");
        const string DeploymentName = "computer-use-preview";

        Console.WriteLine("â•”â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•—");
        Console.WriteLine("â•‘   ðŸš€ REAL Computer Use Demo - AI Controlling Live Browser    â•‘");
        Console.WriteLine("â•šâ•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•\n");

        Console.WriteLine("ðŸ“‹ Task: Search Wikipedia for 'Artificial Intelligence' and extract:");
        Console.WriteLine("   â€¢ Main definition");
        Console.WriteLine("   â€¢ Key applications");
        Console.WriteLine("   â€¢ Historical milestones\n");

        // Install Playwright browsers if needed
        Console.WriteLine("ðŸ”§ Setting up browser automation...");
        Microsoft.Playwright.Program.Main(new[] { "install", "chromium" });

        AIProjectClient aiProjectClient = new(new Uri(endpoint), new AzureCliCredential());

        const string AgentInstructions = @"
You are a web research assistant with the ability to control a web browser.

Your task:
1. Navigate to Wikipedia (you'll see a screenshot of the page)
2. Search for 'Artificial Intelligence'
3. Read the article and extract:
   - The main definition/description
   - 3-5 key applications of AI
   - 2-3 important historical milestones

Be methodical:
- Describe what you see on each screenshot
- Explain each action you're taking
- When you find information, quote it exactly
- Once you have all the information, summarize it clearly
";

        AIAgent agent = await aiProjectClient.CreateAIAgentAsync(
            name: "WikipediaResearchAgent",
            model: DeploymentName,
            instructions: AgentInstructions,
            description: "AI agent that researches topics by browsing Wikipedia.",
            tools: [
