            runResponse = await agent.RunAsync(message, thread: thread, options: runOptions);
        }

        Console.WriteLine("\nâ•”â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•—");
        Console.WriteLine("â•‘                    ðŸ“Š RESEARCH SUMMARY                        â•‘");
        Console.WriteLine("â•šâ•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•\n");
        Console.WriteLine("The AI successfully navigated Wikipedia and controlled the browser!");
        Console.WriteLine($"Total iterations: {iteration}");
        Console.WriteLine("\nPress any key to close the browser...");
        Console.ReadKey();
    }

    private static async Task ExecuteBrowserActionAsync(IPage page, ComputerCallAction action)
    {
        string actionType = action.Kind.ToString();
        Console.WriteLine($"ðŸŽ¬ Executing: {actionType}");

        try
        {
            switch (actionType.ToUpperInvariant())
            {
                case "CLICK":
                    if (action.ClickCoordinate.HasValue)
                    {
                        int x = action.ClickCoordinate.Value.X;
                        int y = action.ClickCoordinate.Value.Y;
                        Console.WriteLine($"   ðŸ–±ï¸  Clicking at ({x}, {y})");
                        await page.Mouse.ClickAsync(x, y);
                    }
                    break;

                case "TYPE":
                    if (!string.IsNullOrEmpty(action.TypeText))
                    {
                        Console.WriteLine($"   âŒ¨ï¸  Typing: '{action.TypeText}'");
                        await page.Keyboard.TypeAsync(action.TypeText, new() { Delay = 50 });
                    }
                    break;

                case "KEYPRESS":
                    if (action.KeyPressKeyCodes != null)
                    {
                        foreach (var key in action.KeyPressKeyCodes)
                        {
                            Console.WriteLine($"   âŒ¨ï¸  Pressing key: {key}");
                            string playwrightKey = ConvertToPlaywrightKey(key);
                            await page.Keyboard.PressAsync(playwrightKey);
                        }
                    }
                    break;

                case "SCREENSHOT":
                    Console.WriteLine("   ðŸ“¸ Taking screenshot");
                    break;

                case "CURSOR":
                    if (action.CursorCoordinate.HasValue)
                    {
                        int x = action.CursorCoordinate.Value.X;
                        int y = action.CursorCoordinate.Value.Y;
                        Console.WriteLine($"   ðŸ–±ï¸  Moving cursor to ({x}, {y})");
                        await page.Mouse.MoveAsync(x, y);
                    }
                    break;

                default:
                    Console.WriteLine($"   âš ï¸  Unknown action type: {actionType}");
                    break;
            }
        }
        catch (Exception ex)
        {
            Console.WriteLine($"   âŒ Error executing action: {ex.Message}");
        }
    }

    private static string ConvertToPlaywrightKey(string key)
    {
        return key.ToUpperInvariant() switch
        {
            "RETURN" or "ENTER" => "Enter",
            "TAB" => "Tab",
            "BACKSPACE" => "Backspace",
            "ESCAPE" or "ESC" => "Escape",
            "SPACE" => "Space",
            _ => key
        };
    }
}
