// REAL Computer Use Demo - AI extracts information from actual web pages
// Task: Fetch real web content and extract structured information

using Azure.AI.Projects;
using Azure.Identity;
using Microsoft.Agents.AI;
using Microsoft.Extensions.AI;
using OpenAI.Responses;
using System.Net.Http;

namespace Demo.ComputerUse;

internal sealed class ProgramRealWeb
{
    private static readonly HttpClient HttpClient = new();

    private static async Task Main(string[] args)
    {
        string endpoint = Environment.GetEnvironmentVariable("AZURE_FOUNDRY_PROJECT_ENDPOINT") ?? throw new InvalidOperationException("AZURE_FOUNDRY_PROJECT_ENDPOINT is not set.");
        const string DeploymentName = "computer-use-preview";

        Console.WriteLine("â•”â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•—");
        Console.WriteLine("â•‘   ðŸŒ REAL Computer Use - AI Analyzing Actual Web Content       â•‘");
        Console.WriteLine("â•šâ•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•\n");

        Console.WriteLine("ðŸ“‹ DEMONSTRATION:");
        Console.WriteLine("   The AI will visit Microsoft's AI blog and extract:");
        Console.WriteLine("   â€¢ Latest article titles");
        Console.WriteLine("   â€¢ Article topics");
        Console.WriteLine("   â€¢ Key announcements\n");

        AIProjectClient aiProjectClient = new(new Uri(endpoint), new AzureCliCredential());

        const string AgentInstructions = @"
You are analyzing a screenshot of a real website.

Your task:
1. Carefully examine the webpage screenshot
2. Identify all article titles or headlines you can see
3. Extract any dates, categories, or topics
4. Summarize the main themes or announcements
5. Present findings in a clear, structured format

Be thorough and accurate - this is REAL web content!
";

        AIAgent agent = await aiProjectClient.CreateAIAgentAsync(
            name: "WebAnalysisAgent",
            model: DeploymentName,
            instructions: AgentInstructions,
            description: "AI agent that analyzes real web page content.",
            tools: [
                ResponseTool.CreateComputerTool(ComputerToolEnvironment.Browser, 1920, 1080).AsAITool(),
            ]);

        try
        {
            await AnalyzeRealWebPageAsync(agent);
        }
        finally
        {
            await aiProjectClient.Agents.DeleteAgentAsync(agent.Name);
        }
    }

    private static async Task AnalyzeRealWebPageAsync(AIAgent agent)
    {
        Console.WriteLine("ðŸŒ Fetching Microsoft AI Blog...");

        // Use a service that converts web pages to screenshots
        // ScreenshotOne API (free tier available)
        string url = "https://blogs.microsoft.com/ai/";
        string screenshotApiUrl = $"https://api.screenshotone.com/take?url={Uri.EscapeDataString(url)}&viewport_width=1920&viewport_height=1080&device_scale_factor=1&format=png&block_ads=true&block_cookie_banners=true&block_trackers=true&access_key=demo"; // Using demo key

        Console.WriteLine($"ðŸ“¸ Capturing screenshot of: {url}");
        Console.WriteLine("â³ This may take a few seconds...\n");

        byte[] screenshot;
        try
        {
            screenshot = await HttpClient.GetByteArrayAsync(screenshotApiUrl);
            Console.WriteLine($"âœ… Screenshot captured ({screenshot.Length / 1024} KB)\n");
        }
        catch (Exception ex)
        {
            Console.WriteLine($"âŒ Failed to capture screenshot: {ex.Message}");
            Console.WriteLine("\nðŸ’¡ ALTERNATIVE: Using pre-captured screenshot from Assets folder...\n");

            // Fallback to existing screenshot
            string assetsPath = Path.Combine(AppDomain.CurrentDomain.BaseDirectory, "Assets", "cua_search_results.png");
            screenshot = await File.ReadAllBytesAsync(assetsPath);
        }

        ChatOptions chatOptions = new();
        ResponseCreationOptions responseCreationOptions = new()
        {
            TruncationMode = ResponseTruncationMode.Auto
        };
        chatOptions.RawRepresentationFactory = (_) => responseCreationOptions;
        ChatClientAgentRunOptions runOptions = new(chatOptions)
        {
            AllowBackgroundResponses = true,
        };

        AgentThread thread = agent.GetNewThread();

        ChatMessage message = new(ChatRole.User, [
            new TextContent($"Analyze this screenshot of Microsoft's AI blog ({url}). Extract all visible article titles, topics, and key information."),
            new DataContent(new BinaryData(screenshot), "image/png")
        ]);

        Console.WriteLine("ðŸ¤– AI is analyzing the web page...\n");
        Console.WriteLine("â•".PadRight(70, 'â•'));

        AgentRunResponse runResponse = await agent.RunAsync(message, thread: thread, options: runOptions);

        // Poll until complete
        while (runResponse.ContinuationToken is { } token)
        {
            await Task.Delay(TimeSpan.FromSeconds(2));
            runOptions.ContinuationToken = token;
            runResponse = await agent.RunAsync(thread, runOptions);
        }

        Console.WriteLine("\nðŸ“Š ANALYSIS RESULTS:\n");

        // Extract all text responses
        var textContents = runResponse.Messages
            .SelectMany(m => m.Contents)
            .OfType<TextContent>();

        foreach (var textContent in textContents)
        {
            if (!string.IsNullOrWhiteSpace(textContent.Text))
            {
                Console.WriteLine(textContent.Text);
                Console.WriteLine();
            }
        }

        // Check if AI wants to take actions
        var computerCalls = runResponse.Messages
            .SelectMany(x => x.Contents)
            .Where(c => c.RawRepresentation is ComputerCallResponseItem)
            .Select(c => (ComputerCallResponseItem)c.RawRepresentation!);

        if (computerCalls.Any())
        {
            Console.WriteLine("\nðŸ’¡ AI suggested actions:");
            foreach (var call in computerCalls)
            {
                Console.WriteLine($"   â€¢ {call.Action.Kind}");
            }
        }

        Console.WriteLine("\nâ•”â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•—");
        Console.WriteLine("â•‘                    âœ… ANALYSIS COMPLETE                          â•‘");
        Console.WriteLine("â•šâ•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•\n");

        Console.WriteLine("ðŸŽ¯ VALUE DEMONSTRATED:");
        Console.WriteLine("   âœ“ AI analyzed REAL web content");
        Console.WriteLine("   âœ“ Extracted structured information from visual layout");
        Console.WriteLine("   âœ“ No web scraping code needed - AI understood the page visually");
        Console.WriteLine("   âœ“ Works even when HTML structure changes!\n");
    }
}
